import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState({
    name: "",
    age: 0,
    date: "",
    programming: "",
  });
  const [loading, setLoading] = useState(true); // Для отслеживания состояния загрузки
  const [error, setError] = useState(null); // Для отслеживания ошибок

  useEffect(() => {
    fetch("/data")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Network response was not ok");
        }
        return res.json();
      })
      .then((data) => {
        setData({
          name: data.Name,
          age: data.Age,
          date: data.Date,
          programming: data.programming,
        });
        setLoading(false); // Загрузка завершена
      })
      .catch((error) => {
        setError(error);
        setLoading(false); // Загрузка завершена с ошибкой
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Показываем сообщение о загрузке
  }

  if (error) {
    return <div>Error: {error.message}</div>; // Показываем сообщение об ошибке
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>React and Flask</h1>
        <p>{data.name}</p>
        <p>{data.age}</p>
        <p>{data.date}</p>
        <p>{data.programming}</p>
      </header>
    </div>
  );
}

export default App;
